import "./registerModal.css";
import React, { useState, useEffect, useRef } from "react";
import Modal from "./Modal";

export default ({ onSubmit }) => {
  const [state, setstate] = useState({
    username: "",
    email: "",
    phoneNumber: "",
  });

  const submitBtn = useRef(null);

  useEffect(() => {
    const _resizeHandler = () => {
      if (submitBtn) {
        submitBtn.current.scrollIntoView();
      }
    };
    window.addEventListener("resize", _resizeHandler);
    return () => window.removeEventListener("resize", _resizeHandler);
  }, []);

  return (
    <Modal id="register-modal">
      <div className="form-container">
        <form
          autoComplete="off"
          onSubmit={async (e) => {
            e.preventDefault();
            onSubmit(state);
          }}
        >
          <div className="logo-container">
            <img src={require("../assets/logo.jpg")} alt="logo" />
          </div>
          <h1>Để lại thông tin cá nhân trước khi bắt đầu MiniGame LẩuPhan</h1>
          <input
            type="text"
            name="username"
            value={state.username}
            onChange={(e) => setstate({ ...state, username: e.target.value })}
            placeholder="Họ và tên..."
            required
          />
          <input
            type="email"
            name="email"
            value={state.email}
            onChange={(e) => setstate({ ...state, email: e.target.value })}
            placeholder="Email..."
            required
          />
          <input
            type="text"
            name="phoneNumber"
            value={state.phoneNumber}
            onChange={(e) =>
              setstate({ ...state, phoneNumber: e.target.value })
            }
            placeholder="Số điện thoại..."
            required
          />
          <button ref={submitBtn} type="submit">
            Chơi ngay!
          </button>
        </form>
      </div>
    </Modal>
  );
};
