const checkAdditionScore = (comboCounter) => {
  if (comboCounter === 10) {
    return 100;
  } else if (comboCounter === 30) {
    return 300;
  } else if (comboCounter >= 50 && comboCounter % 10 === 0) {
    return 500;
  } else {
    return 10;
  }
};

const createScoreCalcSystem = () => {
  return {
    update(delta, worldState, extra) {
      if (worldState.correctCatch) {
        worldState.correctCatch = false;
        worldState.comboCounter += 1;
        const addScore = checkAdditionScore(worldState.comboCounter);
        worldState.score += addScore;
      }
    },
  };
};

export default createScoreCalcSystem;
