import "./index.css";
import {
  createGameLoop,
  createBatch,
  loadTexture,
  loadAtlas,
  createViewport,
} from "gdxjs";
import createWhiteText from "gl-white-texture";
import loadFontFile from "./utils/loadFontFile";

import createStartScreenSystem from "./StartScreen";

const init = async (canvas, infos) => {
  const width = 47;
  const height = 100;
  const viewport = createViewport(canvas, width, height);
  const camera = viewport.getCamera();
  const gl = viewport.getContext();
  const batch = createBatch(gl);
  const whiteTex = createWhiteText(gl);

  const Game = {
    score: 0,
    width,
    height,
    canvas,
    gl,
    batch,
    camera,
    whiteTex,
    viewport,
    setCurrentScreen(screen) {
      if (Game.currentScreen) {
        Game.currentScreen.destroy();
      }
      Game.currentScreen = screen;
    },
  };

  const doAndCount = (promise) =>
    promise.then((result) => {
      return result;
    });

  const [
    gameAssets,
    animAssets,
    backGround,
    newAssets,
    foodInPot,
    startScreen,
    backGround2,
    leaderBoard,
  ] = await Promise.all([
    doAndCount(loadAtlas(gl, "./assets/food_catch.atlas")),
    doAndCount(loadAtlas(gl, "./assets/hotpot.atlas")),
    doAndCount(loadTexture(gl, "./assets/bg.jpg")),
    doAndCount(loadAtlas(gl, "./assets/hotpot-additional.atlas")),
    doAndCount(loadAtlas(gl, "./assets/foodinhotpot.atlas")),
    doAndCount(loadAtlas(gl, "./assets/start_menu.atlas")),
    doAndCount(loadTexture(gl, "./assets/bbg-start.jpg")),
    doAndCount(loadAtlas(gl, "./assets/leaderBoard.atlas")),
  ]);

  let assets = {
    gameAssets,
    animAssets,
    backGround,
    newAssets,
    foodInPot,
    startScreen,
    backGround2,
    leaderBoard,
  };
  Game.assets = assets;

  const numberFont = await loadFontFile(gl, "./fonts/iciel-64.fnt");
  const textFont = await loadFontFile(gl, "./fonts/mijas.fnt");

  Game.font = { numberFont, textFont };
  Game.setCurrentScreen(await createStartScreenSystem(Game));

  gl.clearColor(255 / 255, 114 / 255, 22 / 255, 1);
  const update = (delta) => {
    gl.clear(gl.COLOR_BUFFER_BIT);
    if (Game.currentScreen) {
      Game.currentScreen.update(delta);
    }
  };

  const newGame = createGameLoop(update);
};
if (infos) {
  console.log(infos);
  setInterval(() => {
    infos.innerText = newGame.getFps();
  }, 1000);
}

export default init;
