import { fillRect } from "gdxjs";

const createStunScreenRenderSystem = () => {
  return {
    update(delta, { stunScreen }, { width, height, batch, camera, whiteTex }) {
      if (stunScreen.check) {
        batch.setProjection(camera.combined);
        batch.begin();
        batch.setColor(1, 0, 0, stunScreen.opacity);
        fillRect(batch, whiteTex, 0, 0, width, height);
        batch.end();
        stunScreen.opacity -= delta * 4;
        if (stunScreen.opacity <= 0) {
          stunScreen.opacity = 1;
          stunScreen.check = false;
        }
      }
    },
  };
};

export default createStunScreenRenderSystem;
