import Texture from "kami-texture2";
export default ((gl, url, options) => new Promise(resolve => {
  const image = new Image();
  image.src = url;

  image.onload = () => {
    const texture = new Texture(gl, { ...options,
      image
    });
    texture.setFilter(Texture.Filter.LINEAR, Texture.Filter.LINEAR, false);
    resolve(texture);
  };
}));
export const Filter = Texture.Filter;
