import { AlignMode, TextType } from "../utils/loadFontFile";

const createScoreRenderSystem = () => {
  let scoreText;
  let scoreTexTure;
  let ST_WIDTH, ST_HEIGHT;
  return {
    init(worldState, { font, width, assets }) {
      const { numberFont } = font;
      scoreText = numberFont.setNewText(width);

      const atlas = assets.newAssets;

      scoreTexTure = atlas.findRegion("Score", -1);

      ST_WIDTH = width / 6;
      ST_HEIGHT = (ST_WIDTH * scoreTexTure.height) / scoreTexTure.width;
    },
    update(delta, { score }, { batch, camera, width, height }) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(0, 0, 0, 1);

      scoreTexTure.draw(
        batch,
        width * 0.97 - ST_WIDTH,
        height * 0.11,
        ST_WIDTH,
        ST_HEIGHT
      );

      scoreText.setAlignMode(AlignMode.right);
      scoreText.setType(TextType.dynamic);
      // scoreText.setFixedGlyphWidth(width);
      scoreText.draw(
        batch,
        `${score}`,
        width * 0.975,
        height * 0.12,
        width / 8
      );

      batch.end();
    },
  };
};

export default createScoreRenderSystem;
