const createTrayRenderSystem = () => {
  let tray, trayShadow;
  let TRAY_WIDTH, TRAY_HEIGHT, TS_WIDTH, TS_HEIGHT;
  return {
    init({ trayState }, { width, assets }) {
      let atlas = assets.gameAssets;
      let trayAtlas = assets.foodInPot;

      // tray = atlas.findRegion("tray", -1);
      tray = trayAtlas.findRegion("hot_pot", -1);
      trayShadow = atlas.findRegion("tray-shadow", -1);

      TRAY_WIDTH = width / 3.8;
      TRAY_HEIGHT = (TRAY_WIDTH * tray.height) / tray.width;

      TS_WIDTH = TRAY_WIDTH * 0.8;
      TS_HEIGHT = (TS_WIDTH * trayShadow.height) / trayShadow.width;
    },
    update(delta, { trayState }, { batch, width, height, camera }) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(1, 1, 1, 1);

      tray.draw(
        batch,
        trayState.x - TRAY_WIDTH / 2,
        trayState.y,
        TRAY_WIDTH,
        TRAY_HEIGHT
      );

      trayShadow.draw(
        batch,
        trayState.x - TS_WIDTH / 2,
        trayState.y + height / 13,
        TS_WIDTH,
        TS_HEIGHT
      );

      batch.end();
    },
  };
};

export default createTrayRenderSystem;
