import { createAnimation } from "gdxjs";

export const getFoodInPotTexture = (atlas) => {
  const foodTex = [
    {
      image: atlas.findRegion("Tom", -1),
      index: 0,
    },
    {
      image: atlas.findRegion("kimchi", -1),
      index: 1,
    },
    {
      image: atlas.findRegion("nam", -1),
      index: 2,
    },
    {
      image: atlas.findRegion("ngao", -1), // dung ra la ngao
      index: 3,
    },
    {
      image: atlas.findRegion("raucai", -1),
      index: 4,
    },
    {
      image: atlas.findRegion("thitbo", -1),
      index: 5,
    },
    {
      image: atlas.findRegion("bachtuoc", -1),
      index: 6,
    },
  ];
  return foodTex;
};

export const getFoodTexture = (atlas) => {
  const foodTex = [
    {
      image: atlas.findRegion("tom xuong", -1),
      index: 0,
    },
    {
      image: atlas.findRegion("kimchi xuong", -1),
      index: 1,
    },
    {
      image: atlas.findRegion("nam xuong", -1),
      index: 2,
    },
    {
      image: atlas.findRegion("ngao xuong", -1),
      index: 3,
    },
    {
      image: atlas.findRegion("raucai xuong", -1),
      index: 4,
    },
    {
      image: atlas.findRegion("thitbo xuong", -1),
      index: 5,
    },
    {
      image: atlas.findRegion("bachtuoc xuong", -1),
      index: 6,
    },
  ];
  return foodTex;
};

export const getFoodAnimation = (atlas) => {
  const duration_smoke = 0.05;
  const duration_food = 0.05;
  const effect = atlas.findRegions("effect boc khoi");
  const tom_efftect = atlas.findRegions("Tom");
  const kimchi_efftect = atlas.findRegions("kimchi");
  const nam_efftect = atlas.findRegions("nam");
  const ngao_efftect = atlas.findRegions("ngao");
  const raucai_efftect = atlas.findRegions("raucai");
  const thitbo_efftect = atlas.findRegions("thitbo");
  const bachtuoc_effect = atlas.findRegions("bachtuoc");
  const smoke_animation = createAnimation(duration_smoke, effect);

  const foodAnimation = [
    {
      animation: createAnimation(duration_food, tom_efftect),
      index: 0,
      length: tom_efftect.length - 1,
      duration: duration_food,
    },
    {
      animation: createAnimation(duration_food, kimchi_efftect),
      index: 1,
      length: kimchi_efftect.length - 1,
      duration: duration_food,
    },
    {
      animation: createAnimation(duration_food, nam_efftect),
      index: 2,
      length: nam_efftect.length - 1,
      duration: duration_food,
    },
    {
      animation: createAnimation(duration_food, ngao_efftect),
      index: 3,
      length: ngao_efftect.length - 1,
      duration: duration_food,
    },
    {
      animation: createAnimation(duration_food, raucai_efftect),
      index: 4,
      length: raucai_efftect.length - 1,
      duration: duration_food,
    },
    {
      animation: createAnimation(duration_food, thitbo_efftect),
      index: 5,
      length: thitbo_efftect.length - 1,
      duration: duration_food,
    },
    {
      animation: createAnimation(duration_food, bachtuoc_effect),
      index: 6,
      length: bachtuoc_effect.length - 1,
      duration: duration_food,
    },
  ];
  return {
    foodAnimation,
    smokeAnimation: {
      animation: smoke_animation,
      duration: duration_smoke,
      frameLength: effect.length - 1,
    },
  };
};
