import "./index.css";
import {
  createGameLoop,
  createBatch,
  loadTexture,
  loadAtlas,
  createViewport,
  fillRect,
} from "gdxjs";
import createWhiteText from "gl-white-texture";
import loadFontFile from "./utils/loadFontFile";

import createStartScreenSystem from "./StartScreen";

const init = async (canvas, infos) => {
  const width = 47;
  const height = 100;
  const viewport = createViewport(canvas, width, height);
  const camera = viewport.getCamera();
  const gl = viewport.getContext();
  const batch = createBatch(gl);
  const whiteTex = createWhiteText(gl);

  const Game = {
    score: 0,
    width,
    height,
    canvas,
    gl,
    batch,
    camera,
    whiteTex,
    viewport,
    setCurrentScreen(screen) {
      if (Game.currentScreen) {
        Game.currentScreen.destroy();
      }
      Game.currentScreen = screen;
    },
  };

  const foxLogo = await loadTexture(gl, "./assets/logo.png");
  const LOGO_WIDTH = width / 1.8;
  const LOGO_HEIGHT = (LOGO_WIDTH * foxLogo.height) / foxLogo.width;

  let counter = 0;
  const numOfAssets = 10;

  const draw = () => {
    batch.setProjection(camera.combined);
    batch.begin();
    batch.setColor(255 / 255, 114 / 255, 22 / 255, 1);
    fillRect(batch, whiteTex, 0, 0, width, height);
    batch.setColor(1, 1, 1, 1);
    fillRect(
      batch,
      foxLogo,
      width / 2 - LOGO_WIDTH / 2,
      (height * 0.8) / 3,
      LOGO_WIDTH,
      LOGO_HEIGHT
    );
    batch.setColor(71 / 255, 71 / 255, 71 / 255, 1);
    fillRect(
      batch,
      whiteTex,
      width / 2 - width / (1.45 * 2),
      (height * 2.5) / 4 - height / 50,
      width / 1.45,
      height / 25
    );
    batch.setColor(255 / 255, 114 / 255, 22 / 255, 1);
    fillRect(
      batch,
      whiteTex,
      width / 2 - width / 3,
      (height * 2.5) / 4 - height / 60,
      width / 1.5,
      height / 30
    );
    batch.setColor(255 / 255, 114 / 255, 22 / 255, 1);
    let ratio = counter / numOfAssets;
    fillRect(
      batch,
      whiteTex,
      width / 2 - width / (1.55 * 2),
      (height * 2.5) / 4 - height / 80,
      (width / 1.55) * ratio,
      height / 40
    );
    batch.end();
  };

  const doAndCount = (promise) =>
    promise.then((result) => {
      draw();
      counter += 1;
      return result;
    });

  const [
    gameAssets,
    animAssets,
    backGround,
    newAssets,
    foodInPot,
    startScreen,
    backGround2,
    leaderBoard,
    numberFont,
    textFont,
  ] = await Promise.all([
    doAndCount(loadAtlas(gl, "./assets/food_catch.atlas")),
    doAndCount(loadAtlas(gl, "./assets/hotpot.atlas")),
    doAndCount(loadTexture(gl, "./assets/bg.jpg")),
    doAndCount(loadAtlas(gl, "./assets/hotpot-additional.atlas")),
    doAndCount(loadAtlas(gl, "./assets/foodinhotpot.atlas")),
    doAndCount(loadAtlas(gl, "./assets/start_menu.atlas")),
    doAndCount(loadTexture(gl, "./assets/bbg-start.jpg")),
    doAndCount(loadAtlas(gl, "./assets/leaderBoard.atlas")),
    doAndCount(loadFontFile(gl, "./fonts/iciel-64.fnt")),
    doAndCount(loadFontFile(gl, "./fonts/mijas.fnt")),
  ]);

  let assets = {
    gameAssets,
    animAssets,
    backGround,
    newAssets,
    foodInPot,
    startScreen,
    backGround2,
    leaderBoard,
  };
  Game.assets = assets;

  Game.font = { numberFont, textFont };
  Game.setCurrentScreen(await createStartScreenSystem(Game));

  gl.clearColor(255 / 255, 114 / 255, 22 / 255, 1);
  const update = (delta) => {
    gl.clear(gl.COLOR_BUFFER_BIT);
    if (Game.currentScreen) {
      Game.currentScreen.update(delta);
    }
  };

  const newGame = createGameLoop(update);

  setInterval(() => {
    infos.innerText = newGame.getFps();
  }, 1000);
};

export default init;
