import React, { useState } from "react";
import Modal from "./Modal";
export default ({ onSubmit, setScore }) => {
  const [score, setPlayerScore] = useState("");
  return (
    <Modal>
      <form
        autoComplete="off"
        onSubmit={(e) => {
          e.preventDefault();
          onSubmit(score);
          setScore(score);
        }}
      >
        <input
          placeholder="Nhập số điểm"
          name="score"
          onChange={(e) => setPlayerScore(e.target.value)}
          value={score}
        />
      </form>
    </Modal>
  );
};
