const createLifeRenderSystem = () => {
  let redLife,
    blackLife,
    LIFE_WIDTH,
    LIFE_HEIGHT,
    LIFE_START_X,
    LIFE_START_Y,
    LIFE_MARGIN_RIGHT;
  let tex;

  return {
    init({ trayState }, { width, assets, height }) {
      const atlas = assets.foodInPot;

      redLife = atlas.findRegion("tim_do", -1);
      blackLife = atlas.findRegion("tim_den", -1);

      LIFE_WIDTH = width / 12;
      LIFE_HEIGHT = (LIFE_WIDTH * redLife.height) / redLife.width;

      LIFE_START_X = width * 0.3;
      LIFE_START_Y = height / 9;

      LIFE_MARGIN_RIGHT = width / 9;
    },
    update(delta, { life }, { batch, width, height, camera }) {
      batch.setProjection(camera.combined);
      batch.begin();
      batch.setColor(1, 1, 1, 1);

      for (let i = 0; i < life.length; i++) {
        if (life[i].red) {
          tex = redLife;
        } else {
          tex = blackLife;
        }
        tex.draw(
          batch,
          LIFE_START_X + LIFE_MARGIN_RIGHT * i,
          LIFE_START_Y,
          LIFE_WIDTH,
          LIFE_HEIGHT
        );
      }

      batch.end();
    },
  };
};

export default createLifeRenderSystem;
