import axios from "../../utils/axios";
import { createViewportAwareInputHandler, fillRect, Vector2 } from "gdxjs";
import createStartScreenSystem from "./StartScreen";
import { checkPointInsideRect } from "./utils/coordUtils";

const getHeightBaseOnWidth = (texture, textWidth) => {
  return [textWidth, (textWidth * texture.height) / texture.width];
};

const createLeaderBoardSystem = (Game) => {
  const {
    canvas,
    viewport,
    width,
    height,
    camera,
    batch,
    assets,
    score,
    whiteTex,
    font,
  } = Game;

  const goToStartScreen = async () => {
    Game.setCurrentScreen(await createStartScreenSystem(Game));
  };

  const inputHandler = createViewportAwareInputHandler(canvas, viewport);

  const bg = assets.backGround;
  const atlas = assets.leaderBoard;
  const logo = atlas.findRegion("logo-2", -1);
  const header = atlas.findRegion("header", -1);
  const footer = atlas.findRegion("footer", -1);
  const yourScore = atlas.findRegion("your-score", -1);
  const no1 = atlas.findRegion("no1", -1);
  const no2 = atlas.findRegion("no2", -1);
  const retry = atlas.findRegion("thu-lai", -1);

  const [LOGO_WIDTH, LOGO_HEIGHT] = getHeightBaseOnWidth(logo, width / 4.1);
  const [HEADER_WIDTH, HEADER_HEIGHT] = getHeightBaseOnWidth(header, width / 2);
  const [FOOTER_WIDTH, FOOTER_HEIGHt] = getHeightBaseOnWidth(
    footer,
    width * 0.9
  );
  const [S_WIDTH, S_HIEGHT] = getHeightBaseOnWidth(yourScore, width / 2);
  const [NO1_WIDTH, NO1_HEIGHT] = getHeightBaseOnWidth(no1, width / 8);
  const [NO2_WIDTH, NO2_HEIGHT] = getHeightBaseOnWidth(no2, width / 8);
  const [BUTTON_WIDTH, BUTTON_HEIGHT] = getHeightBaseOnWidth(retry, width / 3);

  const BUTTON_RECTS = [
    width / 2 - BUTTON_WIDTH / 2,
    (height * 2.2) / 3,
    BUTTON_WIDTH,
    BUTTON_HEIGHT,
  ];

  const medalList = [
    { texture: no1, width: NO1_WIDTH, height: NO1_HEIGHT },
    { texture: no2, width: NO2_WIDTH, height: NO2_HEIGHT },
  ];

  const CONTAINER_MARGIN = width / 17;
  const CONTAINER_WIDTH = width - CONTAINER_MARGIN * 2;
  const CONTAINER_HEIGHT = height - CONTAINER_MARGIN * 2;

  let players = [];
  const getData = async () => {
    try {
      const res = await axios.post(
        "/leader-board",
        { score },
        {
          headers: { "x-access-token": localStorage.getItem("@Token") },
        }
      );
      players = res.data;
    } catch (e) {}
  };
  getData();

  let coord = new Vector2();
  inputHandler.addEventListener("touchStart", () => {
    coord.setVector(inputHandler.getTouchedWorldCoord());
    if (checkPointInsideRect(coord, BUTTON_RECTS)) {
      Game.score = 0;
      goToStartScreen();
    }
  });

  const { numberFont, textFont } = font;
  const userScore = numberFont.setNewText(width);
  const scoreSize = width / 5.3;

  const RESULT_START_X = width / 3;
  const NAME_START_Y = (height * 0.6) / 3;
  const SCORE_START_Y = (height * 0.69) / 3;

  const userNameText = [
    textFont.setNewText(width),
    textFont.setNewText(width),
    textFont.setNewText(width),
  ];
  const userScoreText = [
    numberFont.setNewText(width),
    numberFont.setNewText(width),
    numberFont.setNewText(width),
  ];
  const userNameSize = width / 11;
  const userScoreSize = width / 8;
  let loopLength = 0; // top 3 player ll be display

  const update = (delta) => {
    batch.setProjection(camera.combined);
    batch.begin();
    fillRect(batch, bg, 0, 0, width, height);
    batch.setColor(0, 0, 0, 1);
    fillRect(
      batch,
      whiteTex,
      width / 2 - (CONTAINER_WIDTH + width / 100) / 2,
      height / 2 - (CONTAINER_HEIGHT + width / 100) / 2,
      CONTAINER_WIDTH + width / 100,
      CONTAINER_HEIGHT + width / 100
    );
    batch.setColor(255 / 255, 246 / 255, 234 / 255, 1);
    fillRect(
      batch,
      whiteTex,
      width / 2 - CONTAINER_WIDTH / 2,
      height / 2 - CONTAINER_HEIGHT / 2,
      CONTAINER_WIDTH,
      CONTAINER_HEIGHT
    );
    batch.setColor(1, 1, 1, 1);
    logo.draw(batch, (width * 1) / 12, height / 50, LOGO_WIDTH, LOGO_HEIGHT);
    header.draw(
      batch,
      (width * 1.1) / 3,
      height / 25,
      HEADER_WIDTH,
      HEADER_HEIGHT
    );
    yourScore.draw(batch, width / 3, (height * 1.7) / 3, S_WIDTH, S_HIEGHT);
    batch.setColor(0, 0, 0, 1);
    userScore.draw(
      batch,
      `${Game.score}`,
      width / 3,
      (height * 1.79) / 3,
      scoreSize
    );
    batch.setColor(1, 1, 1, 1);
    retry.draw(
      batch,
      BUTTON_RECTS[0],
      BUTTON_RECTS[1],
      BUTTON_RECTS[2],
      BUTTON_RECTS[3]
    );
    footer.draw(
      batch,
      width / 2 - FOOTER_WIDTH / 2,
      height - FOOTER_HEIGHt + height / 200,
      FOOTER_WIDTH,
      FOOTER_HEIGHt
    );
    if (players.length > 0) {
      loopLength = players.length > 3 ? 3 : players.length;
      for (let i = 0; i < loopLength; i++) {
        batch.setColor(0, 0, 0, 1);
        userNameText[i].draw(
          batch,
          players[i].username,
          RESULT_START_X,
          NAME_START_Y + (i * height) / 9,
          userNameSize
        );
        batch.setColor(255 / 255, 115 / 255, 23 / 255, 1);
        userScoreText[i].draw(
          batch,
          players[i].score.toString(),
          RESULT_START_X,
          SCORE_START_Y + (i * height) / 9,
          userScoreSize
        );
        if (i === 0 || i == 1) {
          batch.setColor(1, 1, 1, 1);
          medalList[i].texture.draw(
            batch,
            (width * 0.55) / 3,
            (height * 0.6) / 3 + (height / 8.6) * i,
            medalList[i].width,
            medalList[i].height
          );
        }
      }
    }
    batch.setColor(1, 1, 1, 1);

    batch.end();
  };
  return {
    update,
    destroy() {
      inputHandler.cleanup();
    },
  };
};
export default createLeaderBoardSystem;
